var indexing = function () { return 'index_' + Math.floor(Math.random() * 100000) + '_' + Math.floor(Math.random() * 100000); }
function copyToClipboard(text){var dummy=document.createElement("textarea"); document.body.appendChild(dummy); dummy.value=text; dummy.select(); document.execCommand("copy"); document.body.removeChild(dummy);}
$(document).on('keypress', 'input[type=number]', function(e) { if (e.which != 8 && e.which != 0 && (e.which < 48 || e.which > 57)) { return !1 } });
$(document).on('click', '.btn-change-status', function (e){ e.preventDefault(); var $this=$(this); Swal.fire({title: 'Are you sure?', text: "", icon: 'warning', showCancelButton: true, confirmButtonColor: '#3085d6', cancelButtonColor: '#d33', confirmButtonText: 'Yes, change it!'}).then((result)=>{if (result.value){$('.change-status-form-'+$this.data('index')).submit();}});});
$(document).on('change keyup', '.custom-decimal-field', function(e) { e.preventDefault(); var x = parseFloat($(this).val()); $(this).val(x); if (!$.isNumeric(x)) { notify_bar('danger', 'Please enter valid number'); $(this).val(''); } });
$(document).on('click', '.btn-menu', function(e){e.preventDefault(); $('.btn-menu').toggleClass('menu-active'); $('.menu-overlay.menu-mini').toggleClass('menu-open'); $('body').toggleClass('menu-active');});
$(document).on('click', '.btn-copy-to-clipboard', function (e){e.preventDefault(); var $this=$(this); copyToClipboard($this.data('copy')); notify_bar('info', $this.data('title') + ' has been copied to the clipboard.');});